shaders.shader:
attribute vec2 aPosition;

uniform mat4 uMVMatrix;

void main() {
	gl_Position = uMVMatrix * vec4(aPosition.xy, 0.0, 1.0);
}

attribute vec2 aPosition;
attribute vec4 aColor;

uniform mat4 uMVMatrix;
uniform bool uPremultiplied;

varying vec4 vColor;

vec4 color() {
	return uPremultiplied? vec4(aColor.rgb * aColor.a, aColor.a) : aColor;
}

void main() {
	gl_Position = uMVMatrix * vec4(aPosition.xy, 0.0, 1.0);
	vColor = color();
}

attribute vec2 aPosition;
attribute vec2 aTextureCoordinates;

uniform mat4 uMVMatrix;

varying vec2 vTextureCoordinates;

void main() {
	gl_Position = uMVMatrix * vec4(aPosition.xy, 0.0, 1.0);
	vTextureCoordinates = aTextureCoordinates;
}

attribute vec2 aPosition;
attribute vec2 aTextureCoordinates;

uniform mat4 uMVMatrix;

varying vec2 vTextureCoordinates;

void main() {
	gl_Position = uMVMatrix * vec4(aPosition.xy, 0.0, 1.0);
	vTextureCoordinates = aTextureCoordinates;
}

attribute vec2 aPosition;
attribute vec2 aTextureCoordinates;

uniform mat4 uMVMatrix;
uniform vec2 uOffset;

varying vec2 vTextureCoordinates;

void main() {
	gl_Position = uMVMatrix * vec4(aPosition.xy + uOffset.xy, 0.0, 1.0);
	vTextureCoordinates = aTextureCoordinates;
}

#ifdef GL_ES
precision mediump float;
#endif

uniform vec4 uColor;
uniform bool uPremultiplied;

vec4 color() {
	return uPremultiplied? vec4(uColor.rgb * uColor.a, uColor.a) : uColor;
}

void main(void) {
	gl_FragColor = color();
}

#ifdef GL_ES
precision mediump float;
#endif

varying vec4 vColor;

void main(void) {
	gl_FragColor = vColor;
}

#ifdef GL_ES
precision mediump float;
#endif

uniform vec4 uColor;
uniform sampler2D uTexture;
uniform bool uPremultiplied;

varying vec2 vTextureCoordinates;

vec4 color() {
	return uPremultiplied? vec4(uColor.rgb * uColor.a, uColor.a) : uColor;
}

void main(void) {
	gl_FragColor = texture2D(uTexture, vTextureCoordinates) * color();
}

#ifdef GL_ES
precision mediump float;
#endif

uniform vec4 uColor;
uniform sampler2D uTexture;
uniform bool uPremultiplied;

varying vec2 vTextureCoordinates;

void main(void) {
	vec4 pixel = texture2D(uTexture, vTextureCoordinates);
	vec4 result;
	if (uPremultiplied) {
		result = mix(pixel, uColor * pixel.a, uColor.a);
	} else {
		result = mix(pixel, uColor, uColor.a);
	}
	gl_FragColor = vec4(result.rgb, pixel.a);
}

#ifdef GL_ES
precision mediump float;
#endif

uniform sampler2D uTexture;

varying vec2 vTextureCoordinates;

void main(void) {
	gl_FragColor = texture2D(uTexture, vTextureCoordinates); 
}

#ifdef GL_ES
precision mediump float;
#endif

uniform vec4 uColor;
uniform sampler2D uTexture;
uniform vec4 uClip;
uniform bool uPremultiplied;

varying vec2 vTextureCoordinates;

vec4 color() {
	return uPremultiplied? vec4(uColor.rgb * uColor.a, uColor.a) : uColor;
}

bool is_visible(vec2 coordinates) {
	return uClip.x <= coordinates.x && coordinates.x <= uClip.z &&
			uClip.w <= coordinates.y && coordinates.y <= uClip.y;
} 

void main(void) {
	if (is_visible(vTextureCoordinates)) {
		gl_FragColor = texture2D(uTexture, vTextureCoordinates) * color();
	} else {
		gl_FragColor = vec4(0.0, 0.0, 0.0, 0.0);
	}
}

#ifdef GL_ES
precision mediump float;
#endif

uniform vec4 uColor;
uniform sampler2D uTexture;
uniform vec4 uClip;
uniform bool uPremultiplied;

varying vec2 vTextureCoordinates;

bool is_visible(vec2 coordinates) {
	return uClip.x <= coordinates.x && coordinates.x <= uClip.z &&
			uClip.w <= coordinates.y && coordinates.y <= uClip.y;
}

void main(void) {
	if (is_visible(vTextureCoordinates)) {
		vec4 pixel = texture2D(uTexture, vTextureCoordinates);
		vec4 result;
		if (uPremultiplied) {
			result = mix(pixel, uColor * pixel.a, uColor.a);
		} else {
			result = mix(pixel, uColor, uColor.a);
		}
		gl_FragColor = vec4(result.rgb, pixel.a);
	} else {
		gl_FragColor = vec4(0.0, 0.0, 0.0, 0.0);
	}
}

#ifdef GL_ES
precision mediump float;
#endif

uniform sampler2D uTexture;

varying vec2 vTextureCoordinates;

void main(void) {
	gl_FragColor = texture2D(uTexture, vTextureCoordinates);
}
